using System;namespace OpenCVUnity{	public class CvException : ApplicationException	{		public CvException()			: base()		{		}		public CvException(string message)			: base(message)		{ }		public CvException(string messageFormat, params object[] args)			: base(string.Format(messageFormat, args))		{ }		public CvException(string message, Exception innerException)			: base(message, innerException)		{		}	}}